import React, { Component } from 'react';

import io from 'socket.io-client'

import Video from './components/video'
import Videos from './components/videos'


class App extends Component {
  constructor(props) {
    super(props)

      this.state = {
          localStream: null,    // used to hold local stream object to avoid recreating the stream everytime a new offer comes
          remoteStream: null,    // used to hold remote stream object that is displayed in the main screen

          remoteStreams: [],    // holds all Video Streams (all remote streams)
          peerConnections: {},  // holds all Peer Connections
          selectedVideo: null,

          //status: 'Please wait...',

          pc_config : {
              "iceServers": [
                  {
                      urls : 'stun:stun.l.google.com:19302'
                  }
              ]
          },

          sdpConstraints: {
              'mandatory': {
                  'OfferToReceiveAudio': true,
                  'OfferToReceiveVideo': true
              }
          },
      }

      // DONT FORGET TO CHANGE TO YOUR URL
      this.serviceIP = 'https://ninety-crabs-agree.loca.lt/webrtcPeer'

    //  https://reactjs.org/docs/refs-and-the-dom.html
    //this.localVideoref = React.createRef()
    //this.remoteVideoref = React.createRef()

    this.socket = null
    //this.candidates = []
  }

  componentDidMount = () => {

    this.getLocalStream()

    this.socket = io.connect(
        this.serviceIP,
      {
        path: '/webrtc',
        query: {}
      }
    )

    this.socket.on('connection-success', success => {
      console.log(success)
    })

    this.socket.on('offerOrAnswer', (sdp) => {
      this.textref.value = JSON.stringify(sdp)

      // set sdp as remote description
      this.pc.setRemoteDescription(new RTCSessionDescription(sdp))
    })

    this.socket.on('candidate', (candidate) => {
      // console.log('From Peer... ', JSON.stringify(candidate))
      // this.candidates = [...this.candidates, candidate]
      this.pc.addIceCandidate(new RTCIceCandidate(candidate))
    })

      this.socket.on('online-peer', socketID => {
          console.log('connected peers ...', socketID)
        /*
          // create and send offer to the peer (data.socketID)
          // 1. Create new pc
          this.createPeerConnection(socketID, pc => {
              // 2. Create Offer
              if (pc)
                  pc.createOffer(this.state.sdpConstraints)
                      .then(sdp => {
                          pc.setLocalDescription(sdp)

                          this.sendToPeer('offer', sdp, {
                              local: this.socket.id,
                              remote: socketID
                          })
                      })
          })*/
      })

    // const pc_config = null



    // https://developer.mozilla.org/en-US/docs/Web/API/RTCPeerConnection
    // create an instance of RTCPeerConnection
    this.pc = new RTCPeerConnection(this.state.pc_config)

    // triggered when a new candidate is returned
    this.pc.onicecandidate = (e) => {
      // send the candidates to the remote peer
      // see addCandidate below to be triggered on the remote peer
      if (e.candidate) {
        // console.log(JSON.stringify(e.candidate))
        this.sendToPeer('candidate', e.candidate)
      }
    }

    // triggered when there is a change in connection state
    this.pc.oniceconnectionstatechange = (e) => {
      console.log(e)
    }

    // triggered when a stream is added to pc, see below - this.pc.addStream(stream)
    //this.pc.onaddstream = (e) => {
    //  this.remoteVideoref.current.srcObject = e.stream
    //}

    this.pc.ontrack = (e) => {
         debugger
         //this.remoteVideoref.current.srcObject = e.streams[0]

         this.setState({
           remoteStream: e.streams[0]
         })
       }
  }

    getLocalStream = () => {
        // called when getUserMedia() successfully returns - see below
        // getUserMedia() returns a MediaStream object (https://developer.mozilla.org/en-US/docs/Web/API/MediaStream)
        const success = (stream) => {
            window.localStream = stream
            // this.localVideoref.current.srcObject = stream
            this.pc.addStream(stream);
            this.setState({
                localStream: stream
            })

            this.whoisOnline()
        }

        // called when getUserMedia() fails - see below
        const failure = (e) => {
            console.log('getUserMedia Error: ', e)
        }

        // https://developer.mozilla.org/en-US/docs/Web/API/MediaDevices/getUserMedia
        // see the above link for more constraint options
        const constraints = {
            // audio: true,
            video: true,
            // video: {
            //   width: 1280,
            //   height: 720
            // },
            // video: {
            //   width: { min: 1280 },
            // }
            options: {
                mirror: true,
            }
        }

        // https://developer.mozilla.org/en-US/docs/Web/API/MediaDevices/getUserMedia
        navigator.mediaDevices.getUserMedia(constraints)
            .then(success)
            .catch(failure)
    }

    whoisOnline = () => {
        // let all peers know I am joining
        this.sendToPeer('onlinePeers', null, {local: this.socket.id})
    }

  sendToPeer = (messageType, payload, socketID) => {
    this.socket.emit(messageType, {
      socketID,
      payload
    })
  }

  /* ACTION METHODS FROM THE BUTTONS ON SCREEN */

  createOffer = () => {
    console.log('Offer')

    // https://developer.mozilla.org/en-US/docs/Web/API/RTCPeerConnection/createOffer
    // initiates the creation of SDP
    this.pc.createOffer(this.state.sdpConstraints)
      .then(sdp => {
        // console.log(JSON.stringify(sdp))

        // set offer sdp as local description
        this.pc.setLocalDescription(sdp)

        this.sendToPeer('offerOrAnswer', sdp)
    })
  }

  // https://developer.mozilla.org/en-US/docs/Web/API/RTCPeerConnection/createAnswer
  // creates an SDP answer to an offer received from remote peer
  createAnswer = () => {
    console.log('Answer')
    this.pc.createAnswer(this.state.sdpConstraints)
      .then(sdp => {
        // console.log(JSON.stringify(sdp))

        // set answer sdp as local description
        this.pc.setLocalDescription(sdp)

        this.sendToPeer('offerOrAnswer', sdp)
    })
  }

  setRemoteDescription = () => {
    // retrieve and parse the SDP copied from the remote peer
    const desc = JSON.parse(this.textref.value)

    // set sdp as remote description
    this.pc.setRemoteDescription(new RTCSessionDescription(desc))
  }

  addCandidate = () => {
    // retrieve and parse the Candidate copied from the remote peer
    // const candidate = JSON.parse(this.textref.value)
    // console.log('Adding candidate:', candidate)

    // add the candidate to the peer connection
    // this.pc.addIceCandidate(new RTCIceCandidate(candidate))

    this.candidates.forEach(candidate => {
      console.log(JSON.stringify(candidate))
      this.pc.addIceCandidate(new RTCIceCandidate(candidate))
    });
  }

  render() {

      console.log(this.state.localStream)

      const statusText = <div style={{ color: 'yellow', padding: 5 }}>{this.state.status}</div>


      return (
      <div>
        <Video
          videoStyles={{
            zIndex:2,
            position: 'fixed',
            right:0,
            width: 200,
            height: 200,
            margin: 5,
            backgroundColor: 'black'
          }}
          //ref={this.localVideoref}
          videoStream={this.state.localStream}
          autoPlay muted>
        </Video>
        <Video
          videoStyles={{
            zIndex: 1,
            position: 'fixed',
            bottom: 0,
            minWidth: '100%',
            minHeight: '100%',
            backgroundColor: 'black'
          }}
            //ref={ this.remoteVideoref }
            videoStream={this.state.remoteStream}
          autoPlay>
        </Video>
        <br />
          <div style={{
              zIndex: 3,
              position: 'absolute',
              margin: 10,
              backgroundColor: '#cdc4ff4f',
              padding: 10,
              borderRadius: 5,
          }}>
              { statusText }
          </div>
          <div>
              <Videos
                  switchVideo={this.switchVideo}
                  remoteStreams={this.state.remoteStreams}
              ></Videos>
          </div>
          <br />

        <div style={{zIndex: 1, position: 'fixed'}} >
          <button onClick={this.createOffer}>Call</button>
          <button onClick={this.createAnswer}>Answer</button>

          <br />
          <textarea style={{ width: 450, height:40 }} ref={ref => { this.textref = ref }} />
        </div>

          {/*
        <br />
        <button onClick={this.setRemoteDescription}>Set Remote Desc</button>
        <button onClick={this.addCandidate}>Add Candidate</button>
        */}

      </div>
    )
  }
}

export default App;
